mapper.select:
select * from merchant


        (name LIKE CONCAT('%',#{name},'%') or pinyin_inital LIKE CONCAT('%',#{name},'%') or pinyin LIKE CONCAT('%',#{name},'%'))


        and state = #{state}


order by create_date desc  limit #{start} , #{limit}

select count(*) from merchant


        (name LIKE CONCAT('%',#{name},'%') or pinyin_inital LIKE CONCAT('%',#{name},'%') or pinyin LIKE CONCAT('%',#{name},'%'))


        and state = #{state}
